mapper.select:
SELECT ID, TRAIN_DESC, VERSION, BELONG_DATE, DATA_TYPE, START_DATE, END_DATE
FROM VEHICLE_CREW_TRAIN
WHERE DEL_FLAG='0' AND ID=#{id}

SELECT count(1) FROM VEHICLE_CREW_TRAIN
WHERE

    BELONG_DATE=#{belongDate,jdbcType=TIMESTAMP}


    START_DATE=#{startDate,jdbcType=TIMESTAMP} AND END_DATE=#{endDate,jdbcType=TIMESTAMP}


    AND VERSION=#{version,jdbcType=VARCHAR}

AND DEL_FLAG='0' AND rownum=1

mapper.update:
UPDATE VEHICLE_CREW_TRAIN SET
DEL_FLAG='1', UPDATE_BY=#{userId}, UPDATE_DATE=sysdate
WHERE ID IN (

    #{id}

)
