mapper.select:
SELECT ID, FAULT_DESC, OTHER_DESC, VERSION, BELONG_DATE
FROM VEHICLE_DISPATCH_HANDOVER
WHERE DEL_FLAG='0' AND ID=#{id}

select count(1) from VEHICLE_DISPATCH_HANDOVER
where BELONG_DATE=#{belongDate}

    AND VERSION=#{version}

AND DEL_FLAG='0' AND rownum=1

mapper.update:
UPDATE VEHICLE_DISPATCH_HANDOVER SET
DEL_FLAG='1', UPDATE_BY=#{userId}, UPDATE_DATE=sysdate
WHERE ID IN (

    #{id}

)
