mapper.select:
select count(1) from VEHICLE_DISPATCH_RECORD
where BELONG_DATE=#{record.belongDate}

    AND VERSION=#{record.version}

AND DEL_FLAG='0' AND rownum=1

mapper.update:
UPDATE VEHICLE_DISPATCH_RECORD SET
ORDER_NUM=#{orderNum}, VERSION=VERSION+1, UPDATE_BY=#{updateBy}, UPDATE_DATE=sysdate
WHERE ID=#{id} and VERSION=#{version}

UPDATE VEHICLE_DISPATCH_RECORD SET
DEL_FLAG='1', UPDATE_BY=#{userId}, UPDATE_DATE=sysdate
WHERE ID IN (

    #{id}

)

UPDATE VEHICLE_DISPATCH_ORDER SET
DEL_FLAG='1', UPDATE_BY=#{userId}, UPDATE_DATE=sysdate
WHERE RECORD_ID IN (

    #{id}

)
