mapper.select:
SELECT ID, TYPE_CODE, CODE, NAME, REMARK, STATUS, SORT
FROM SYS_DICT
WHERE TYPE_CODE=#{typeCode}

    and CODE=#{code}


    and STATUS=#{status}

ORDER BY SORT asc

SELECT ID, TYPE_CODE, CODE, NAME, REMARK, STATUS, SORT
FROM SYS_DICT
WHERE ID=#{id}

mapper.update:
UPDATE SYS_DICT SET
DEL_FLAG='1', UPDATE_BY=#{userId}, UPDATE_DATE=sysdate
WHERE ID IN (

    #{id}

)
