mapper.select:
SELECT * FROM TRAFFIC_PRODUCTION_RECORD WHERE DEL_FLAG='0' AND DATA_TYPE=#{dataType}
AND STATION_CODE=#{stationCode}
AND START_DATE>=to_date(#{startDate},'yyyy-mm-dd hh24:mi:ss')
AND to_date(#{endDate},'yyyy-mm-dd hh24:mi:ss')>=END_DATE
ORDER BY CREATE_DATE desc

SELECT * FROM TRAFFIC_PRODUCTION_RECORD WHERE DEL_FLAG='0' AND DATA_TYPE=#{dataType}
AND STATION_CODE=#{stationCode}
AND to_date(#{startDate},'yyyy-mm-dd hh24:mi:ss')>=START_DATE
AND END_DATE>=to_date(#{endDate},'yyyy-mm-dd hh24:mi:ss')
ORDER BY START_DATE desc

SELECT COUNT(*) FROM TRAFFIC_PRODUCTION_RECORD WHERE DEL_FLAG='0' AND DATA_TYPE=#{dataType}
AND STATION_CODE=#{stationCode}
AND START_DATE=to_date(#{startDate},'yyyy-mm-dd hh24:mi:ss')
AND END_DATE=to_date(#{endDate},'yyyy-mm-dd hh24:mi:ss')

SELECT t1.*, t2.STATION_NAME FROM TRAFFIC_PRODUCTION_RECORD t1 LEFT JOIN SYS_STATION t2 on t1.STATION_CODE=t2.STATION_CODE WHERE t1.DEL_FLAG='0' AND t1.STATION_CODE=#{stationCode} AND t1.DATA_TYPE=#{dataType}
AND t1.START_DATE>=to_date(#{startDate},'yyyy-mm-dd hh24:mi:ss')
AND to_date(#{endDate},'yyyy-mm-dd hh24:mi:ss')>=t1.END_DATE

mapper.update:
UPDATE TRAFFIC_PRODUCTION_SUMMARY SET
(TYPE1_COUNT,TYPE2_COUNT,TYPE3_COUNT,TYPE4_COUNT,TYPE5_COUNT,TYPE6_COUNT)
=
(
SELECT TYPE1_COUNT,TYPE2_COUNT,TYPE3_COUNT,TYPE4_COUNT,TYPE5_COUNT,TYPE6_COUNT
FROM TRAFFIC_PRODUCTION_RECORD
WHERE ID=#{recordId}
)
WHERE STATION_CODE=#{stationCode}
AND DATA_TYPE=#{dataType}
AND START_DATE=to_date(#{startDate},'yyyy-mm-dd hh24:mi:ss')
AND END_DATE  =to_date(#{endDate},'yyyy-mm-dd hh24:mi:ss')

UPDATE TRAFFIC_PRODUCTION_APPROVAL SET
SUBMIT_STATION=#{submitStation},
UPDATE_DATE=sysdate WHERE ID=#{id}

mapper.select:
SELECT t1.ID, t1.RECORD_ID,t1.STATION_CODE,t1.PRODUCTION_TYPE,t1.TITLE,t1.WORK_TIME,t1.WORK_TIME_END,t1.WORK_CODE,t1.WORK_DEPT,t1.WORK_CONTENT, t1.EVENT_TIME,t1.EVENT_DESC,t1.EVENT_AFFECT,t1.EVENT_HANDLE,t1.PIC_URL,t1.IS_CLOSE,t1.RESULT,t2.STATION_NAME, t1.DATA_TYPE,t1.VERSION FROM TRAFFIC_PRODUCTION_INFO t1 LEFT JOIN SYS_STATION t2 ON t1.STATION_CODE=t2.STATION_CODE AND t2.OPEN_FLAG='1' WHERE t1.DEL_FLAG='0'
AND t1.STATION_CODE=#{stationCode}
AND t1.PRODUCTION_TYPE=#{productionType}
AND t1.DATA_TYPE=#{dataType}
AND t1.DATA_DATE>=to_date(#{startDate},'yyyy-mm-dd hh24:mi:ss')
AND to_date(#{endDate},'yyyy-mm-dd hh24:mi:ss')>=t1.DATA_DATE
AND t1.START_DATE>=to_date(#{startDate},'yyyy-mm-dd hh24:mi:ss')
AND to_date(#{endDate},'yyyy-mm-dd hh24:mi:ss')>=t1.END_DATE
ORDER BY t1.CREATE_DATE desc

