mapper.select:
SELECT ID, WEATHER_INFO, VERSION, DATA_DATE, DATA_TYPE, START_DATE, END_DATE
FROM VEHICLE_BAD_WEATHER
WHERE DEL_FLAG='0' AND ID=#{id}

select count(1) from VEHICLE_BAD_WEATHER
WHERE

    DATA_DATE=#{dataDate,jdbcType=TIMESTAMP}


    START_DATE=#{startDate,jdbcType=TIMESTAMP} and END_DATE=#{endDate,jdbcType=TIMESTAMP}


    AND VERSION=#{version,jdbcType=VARCHAR}

AND DEL_FLAG='0' AND rownum=1

mapper.update:
UPDATE VEHICLE_BAD_WEATHER SET
DEL_FLAG='1', UPDATE_BY=#{userId}, UPDATE_DATE=sysdate
WHERE ID IN (

    #{id}

)
