mapper.select:
SELECT COUNT(*) FROM VEHICLE_DRIVING_RECORD WHERE DEL_FLAG = '0' AND DATA_TYPE=#{dataType}
AND START_DATE = to_date(#{startDate},'yyyy-mm-dd hh24:mi:ss')
AND END_DATE = to_date(#{endDate},'yyyy-mm-dd hh24:mi:ss')

mapper.update:
UPDATE VEHICLE_DRIVING_RECORD SET
REMARK=#{remark},
VERSION = VERSION+1, UPDATE_BY=#{updateBy}, UPDATE_DATE=sysdate WHERE ID=#{id} and VERSION=#{version}

mapper.delete:
DELETE FROM VEHICLE_DRIVING_RECORD
WHERE ID IN (

    #{id}

)
